package br.upe.ppsw.jabberpoint.modelo;

import static org.junit.Assert.assertNotNull;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

import br.upe.ppsw.jabberpoint.base.JabberPointUtil;

public class SlideItemTest {
	private SlideItem item00 = null;
	private SlideItem item01 = null;
	private SlideItem item02 = null;
	private final String TESTE_IMAGE = "";
	byte[] imageByte = JabberPointUtil.decode(TESTE_IMAGE);
	
	@BeforeEach
	public void init() {
		this.item00 = new SlideItem(0);
		this.item01 = new SlideItem(0,"item1");
		this.item02 = new SlideItem(0, "item2", imageByte);
	}
	
	@Test
	public void testeConstrutorComLevelComSucesso() {
		Assertions.assertAll(() -> assertNotNull(this.item00.getLevel()),
				() -> assertNotNull(this.item00.getText()),
				() -> assertNotNull(this.item00.getType()));
	}
	
	@Test
	public void testeConstrutorLevelTituloComSucesso() {
		Assertions.assertAll(() -> assertNotNull(this.item01.getLevel()),
				() -> assertNotNull(this.item01.getText()),
				() -> assertNotNull(this.item01.getType()));
	}
	
	@Test
	public void testeConstrutorLevelTituloImagemComSucesso() {
		Assertions.assertAll(() -> assertNotNull(this.item02.getLevel()),
				() -> assertNotNull(this.item02.getText()),
				() -> assertNotNull(this.item02.getMedia()));
	}
}
